import React, { useState, useEffect } from "react";
import axios from "axios";
import ReactMarkdown from "react-markdown";
import { ClipLoader } from "react-spinners";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMicrophone, faPlay, faPause, faRedo, faArrowRight } from '@fortawesome/free-solid-svg-icons';
import loadingGif from './assets/Loading.gif'; // Import the loading gif file
import backgroundGif from './assets/Background.gif'; // Import the background gif file

function App() {
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [generatingAnswer, setGeneratingAnswer] = useState(false);
  const [showResults, setShowResults] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);
  const [speechSynthesisUtterance, setSpeechSynthesisUtterance] = useState(null);

  const API_KEY = "AIzaSyBhuYRkdE9ULfxl3w0iDkrOLGkbt7_zUHc"; // Your API key

  useEffect(() => {
    const greetUser = () => {
      const greetingMessage = "Hi! This is Take Care Guru. How can I help you today?";
      speakText(greetingMessage);
    };
    greetUser();
  }, []);

  async function generateAnswer(e) {
    e.preventDefault();
    setGeneratingAnswer(true);
    setAnswer("Loading health information...");

    try {
      const response = await axios({
        url: `https://gemini.googleapis.com/v1/your-endpoint`,
        method: "POST",
        headers: {
          "Authorization": `Bearer ${API_KEY}`,
          "Content-Type": "application/json",
        },
        data: {
          prompt: question, // Adjust based on your API requirements
        },
      });

      const { healthTips, predictions, care } = response.data; // Adjust based on actual API response structure

      const generatedAnswer = `
        **Health Tips:** ${healthTips}  
        **Predictions:** ${predictions}  
        **Care Information:** ${care}
      `;

      setAnswer(generatedAnswer);
      setQuestion(""); // Clear the question field
    } catch (error) {
      console.error(error);
      setAnswer("Sorry - Something went wrong. Please try again!");
    }

    setGeneratingAnswer(false);
    setShowResults(true);
  }

  const startVoiceRecognition = () => {
    if (!('SpeechRecognition' in window) && !('webkitSpeechRecognition' in window)) {
      alert('Speech Recognition API is not supported in this browser.');
      return;
    }

    const recognition = new (window.SpeechRecognition || window.webkitSpeechRecognition)();
    recognition.lang = 'en-US';
    recognition.interimResults = false;
    recognition.maxAlternatives = 1;

    recognition.start();

    recognition.onstart = () => {
      console.log('Voice recognition started. Speak now.');
    };

    recognition.onresult = (event) => {
      const speechToText = event.results[0][0].transcript;
      setQuestion(speechToText);
    };

    recognition.onerror = (event) => {
      console.error('Speech recognition error:', event.error);
    };

    recognition.onend = () => {
      console.log('Voice recognition ended.');
    };
  };

  const speakText = (text, lineByLine = false) => {
    if (isPlaying) return;

    const utterance = new SpeechSynthesisUtterance();
    utterance.lang = 'en-US';
    utterance.voice = speechSynthesis.getVoices().find(voice => voice.name.includes('Male')) || null;
    const lines = text.split('\n');

    setSpeechSynthesisUtterance(utterance);
    setIsPlaying(true);

    if (lineByLine) {
      let currentLine = 0;
      const speakNextLine = () => {
        if (currentLine < lines.length) {
          utterance.text = lines[currentLine];
          utterance.onend = speakNextLine;
          speechSynthesis.speak(utterance);
          currentLine++;
        } else {
          setIsPlaying(false);
        }
      };
      speakNextLine();
    } else {
      utterance.text = text;
      utterance.onend = () => setIsPlaying(false);
      speechSynthesis.speak(utterance);
    }
  };

  const handlePlayResponse = () => {
    speakText(answer, true);
  };

  const handlePauseResponse = () => {
    if (speechSynthesisUtterance) {
      speechSynthesis.pause();
      setIsPlaying(false);
    }
  };

  const handleRestart = () => {
    setQuestion("");
    setAnswer("");
    setGeneratingAnswer(false);
    setShowResults(false);
    setIsPlaying(false);
    setSpeechSynthesisUtterance(null);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      generateAnswer(e);
    }
  };

  return (
    <>
      {/* Loading GIF */}
      {generatingAnswer && (
        <div className="fixed inset-0 flex justify-center items-center bg-gray-800 bg-opacity-30">
          <img 
            src={loadingGif} 
            alt="Loading" 
            className="w-full h-full object-cover" 
          />
        </div>
      )}

      {/* Main Container */}
      {!generatingAnswer && (
        <div
          className={`h-screen p-3 flex flex-col justify-center items-center overflow-hidden ${
            showResults ? "justify-start" : ""
          }`}
          style={{
            backgroundImage: `url(${backgroundGif})`,
            backgroundSize: "cover",
            backgroundPosition: "center",
          }}
        >
          <form
            onSubmit={generateAnswer}
            className={`w-full md:w-2/3 lg:w-1/2 xl:w-1/3 text-center rounded-lg shadow-lg bg-white py-8 px-5 transition-all duration-500 transform hover:scale-105 bg-opacity-80 backdrop-blur-md ${
              showResults ? "absolute left-0" : ""
            }`}
            style={{ boxShadow: '0px 6px 20px rgba(0, 0, 0, 0.1)', backgroundColor: 'rgba(255, 255, 255, 0.9)' }}
          >
            <h1 className="text-6xl font-bold text-blue-500 mb-7 animate-bounce">
              Take Care Guru
            </h1>
            <div className="relative">
              <textarea
                required
                className="border border-gray-300 rounded w-full my-2 min-h-fit p-3 transition-all duration-300 focus:border-blue-400 focus:shadow-lg"
                value={question}
                onChange={(e) => setQuestion(e.target.value)}
                onKeyPress={handleKeyPress} // Handle Enter key press
                placeholder="Ask about health..."
                style={{ resize: 'none', minHeight: '100px', padding: '10px' }}
              ></textarea>
              <button
                type="button"
                onClick={startVoiceRecognition}
                className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-600 hover:text-gray-900"
                aria-label="Start Voice Recognition"
              >
                <FontAwesomeIcon icon={faMicrophone} size="lg" />
              </button>
              <button
                type="button"
                onClick={generateAnswer} // Add arrow button to submit form for voice input
                className="absolute right-10 top-1/2 transform -translate-y-1/2 text-gray-600 hover:text-gray-900"
                aria-label="Get Response"
              >
                <FontAwesomeIcon icon={faArrowRight} size="lg" />
              </button>
            </div>
            <div className="flex items-center justify-center space-x-2 mt-4">
              <button
                type="button"
                onClick={handleRestart}
                className="bg-gray-500 text-white py-2 px-4 rounded-md shadow-md hover:bg-gray-600 transition-all duration-300"
                aria-label="Restart Application"
              >
                <FontAwesomeIcon icon={faRedo} size="lg" />
              </button>
              <button
                type="submit"
                className={`bg-blue-500 text-white py-2 px-4 rounded-md shadow-md hover:bg-green-600 transition-all duration-300 ${
                  generatingAnswer ? "opacity-50 cursor-not-allowed" : ""
                }`}
                disabled={generatingAnswer}
              >
                {generatingAnswer ? <ClipLoader size={20} color={"#fff"} /> : "Generate Answer"}
              </button>
            </div>
          </form>
          {showResults && (
            <div className="absolute right-0 flex flex-col space-y-4 p-4">
              <div
                className="w-full md:w-[400px] lg:w-[600px] xl:w-[800px] p-5 bg-white rounded-lg shadow-md border border-gray-200"
                style={{
                  border: '1px solid rgba(0, 0, 0, 0.1)', 
                  borderRadius: '10px', 
                  background: 'rgba(255, 255, 255, 0.9)', 
                  maxHeight: '400px', 
                }}
              >
                <ReactMarkdown className="text-left text-lg leading-relaxed">
                  {answer}
                </ReactMarkdown>
                <div className="flex justify-between mt-4">
                  <button
                    type="button"
                    onClick={handlePlayResponse}
                    className="bg-green-500 text-white py-2 px-4 rounded-md shadow-md hover:bg-green-600 transition-all duration-300"
                    aria-label="Play Response"
                  >
                    <FontAwesomeIcon icon={faPlay} size="lg" />
                  </button>
                  <button
                    type="button"
                    onClick={handlePauseResponse}
                    className="bg-yellow-500 text-white py-2 px-4 rounded-md shadow-md hover:bg-yellow-600 transition-all duration-300"
                    aria-label="Pause Response"
                  >
                    <FontAwesomeIcon icon={faPause} size="lg" />
                  </button>
                </div>
              </div>
            </div>
          )}
        </div>
      )}
    </>
  );
}

export default App;
