import React from 'react';
import ReactMarkdown from 'react-markdown';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlay, faPause } from '@fortawesome/free-solid-svg-icons';

function AnswerContainer({ answer, onPlay, onPause }) {
  return (
    <div className="answer-container bg-white shadow-lg p-4 rounded-lg">
      <div className="flex justify-between items-center mb-2">
        <h2 className="text-2xl font-bold text-gray-700">Generated Answer</h2>
        <div className="flex space-x-2">
          <button onClick={onPlay} className="text-gray-600 hover:text-gray-900">
            <FontAwesomeIcon icon={faPlay} size="lg" />
          </button>
          <button onClick={onPause} className="text-gray-600 hover:text-gray-900">
            <FontAwesomeIcon icon={faPause} size="lg" />
          </button>
        </div>
      </div>
      <ReactMarkdown>{answer}</ReactMarkdown>
    </div>
  );
}

export default AnswerContainer;
