import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AnswerContainer from './components/AnswerContainer';
import VoiceButton from './components/VoiceButton';
import { ClipLoader } from 'react-spinners';
import './App.css';

function App() {
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [generatingAnswer, setGeneratingAnswer] = useState(false);
  const [showResults, setShowResults] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);

  const API_KEY = 'AIzaSyBhuYRkdE9ULfxl3w0iDkrOLGkbt7_zUHc';

  async function generateAnswer(e) {
    e.preventDefault();
    setGeneratingAnswer(true);
    setAnswer('Loading health tips... Please wait a moment.');
    setShowResults(false);

    try {
      const response = await axios.post(`https://generativelanguage.googleapis.com/v1beta/models/gemini-pro:generateContent?key=${API_KEY}`, {
        contents: [{ parts: [{ text: question }] }],
      });

      const generatedAnswer = response.data.candidates[0].content.parts[0].text;
      setAnswer(generatedAnswer);
      setQuestion('');
    } catch (error) {
      setAnswer('Sorry - Something went wrong. Please try again!');
    }

    setGeneratingAnswer(false);
    setShowResults(true);
  }

  const startVoiceRecognition = () => {
    if (!('SpeechRecognition' in window) && !('webkitSpeechRecognition' in window)) {
      alert('Speech Recognition API is not supported in this browser.');
      return;
    }

    const recognition = new (window.SpeechRecognition || window.webkitSpeechRecognition)();
    recognition.lang = 'en-US';
    recognition.interimResults = false;
    recognition.maxAlternatives = 1;

    recognition.start();

    recognition.onresult = (event) => {
      setQuestion(event.results[0][0].transcript);
    };

    recognition.onerror = (event) => {
      console.error('Speech recognition error:', event.error);
    };
  };

  const handlePlayResponse = () => {
    const utterance = new SpeechSynthesisUtterance(answer);
    utterance.lang = 'en-US';
    speechSynthesis.speak(utterance);
    setIsPlaying(true);
    utterance.onend = () => setIsPlaying(false);
  };

  const handlePauseResponse = () => {
    speechSynthesis.pause();
    setIsPlaying(false);
  };

  const handleRestart = () => {
    setQuestion('');
    setAnswer('');
    setGeneratingAnswer(false);
    setShowResults(false);
    setIsPlaying(false);
  };

  return (
    <div className="app-container" style={{ backgroundImage: `url(${process.env.PUBLIC_URL + '/assets/Background.gif'})` }}>
      {generatingAnswer && (
        <div className="fixed inset-0 flex justify-center items-center bg-gray-800 bg-opacity-30">
          <img src={process.env.PUBLIC_URL + '/assets/Loading.gif'} alt="Loading" className="w-full h-full object-cover" />
        </div>
      )}

      <form onSubmit={generateAnswer} className="form-container">
        <h1 className="app-title">Take Care Guru</h1>
        <div className="relative">
          <textarea
            required
            className="question-input"
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            placeholder="Enter your health issue or problem"
          />
          <VoiceButton onClick={startVoiceRecognition} />
          <button type="submit" className="submit-button" disabled={generatingAnswer}>
            {generatingAnswer ? <ClipLoader size={20} color="#fff" /> : 'Generate Answer'}
          </button>
        </div>
        <button type="button" onClick={handleRestart} className="restart-button">
          Restart
        </button>
      </form>

      {showResults && (
        <AnswerContainer
          answer={answer}
          onPlay={handlePlayResponse}
          onPause={handlePauseResponse}
        />
      )}
    </div>
  );
}

export default App;
